//! Test support library for Reinhardt framework.
//!
//! This crate provides shared test utilities and generated code
//! (such as gRPC proto definitions) used by integration tests and benchmarks.

// Proto definitions (generated by tonic-build)
pub mod proto {
	pub mod common {
		tonic::include_proto!("reinhardt.common");
	}

	pub mod user {
		tonic::include_proto!("reinhardt.user");
	}

	pub mod user_events {
		tonic::include_proto!("reinhardt.user_events");
	}
}

// gRPC service implementations for testing
pub mod grpc_services {
	pub mod user_service {
		include!("../grpc_services/user_service.rs");
	}

	pub mod user_events_service {
		include!("../grpc_services/user_events_service.rs");
	}

	// Re-export commonly used types
	pub use user_events_service::{UserEventsBroadcaster, UserEventsServiceImpl};
	pub use user_service::{UserServiceImpl, UserStorage};
}
